function Box(){
  return <>

<body>
    <header>
        <h1>Your Name</h1>
        <p>Web Developer & Designer</p>
    </header>
    <div class="container">
        <div class="section">
            <h2>About Me</h2>
            <p>
                I am a passionate web developer and designer with a strong background in creating beautiful and user-friendly websites. I am dedicated to delivering high-quality digital solutions.
            </p>
        </div>
        <div class="section">
            <h2>Skills</h2>
            <ul>
                <li>HTML5, CSS3, JavaScript</li>
                <li>Responsive Web Design</li>
                <li>UI/UX Design</li>
                <li>Front-end Frameworks (e.g., React, Angular)</li>
                <li>Graphic Design (Photoshop, Illustrator)</li>
            </ul>
        </div>
        <div class="section">
            <h2>Portfolio</h2>
            <p>Here are some of my recent projects:</p>
            <ul>
                <li><a href="#">Project 1</a></li>
                <li><a href="#">Project 2</a></li>
                <li><a href="#">Project 3</a></li>
            </ul>
        </div>
        <div class="section">
            <h2>Contact Information</h2>
            <p>Email: your.email@example.com</p>
            <p>Phone: (123) 456-7890</p>
            <p>LinkedIn: www.linkedin.com/in/yourname</p>
        </div>
    </div>
</body>
</>
}


