import { useState } from "react";

function App() {
  const [show, setShow] = useState(true);
  return (
    <div className="App">
      <button onClick={() => setShow(!show)}>
        {show ? "Hide" : "Show"} component
      </button>
      {show ? <Box /> : null}
    </div>
  );
}

function Box() {
  return <div className="box"></div>;
}


const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<Box />);
